import React, { useState, useEffect } from 'react';
import api from '../api';
import socket from '../socket';
import PostInput from './PostInput';

export default function CommunityFeed({ community }){
  const [posts, setPosts] = useState([]);

  useEffect(()=>{
    if(!community) return;
    // fetch history
    let cancelled = false;
    api.get('/posts?community=' + community._id).then(r=>{
      if(!cancelled) setPosts(r.data);
    }).catch(()=>{});
    // connect socket and join room
    const token = localStorage.getItem('token');
    socket.connect();
    socket.emit('join_community', { communityId: community._id, token });
    socket.on('new_post', p=>{
      setPosts(prev => [p, ...prev]);
    });
    return ()=> {
      cancelled = true;
      socket.off('new_post');
      socket.disconnect();
    };
  }, [community]);

  return <div>
    <h4>Feed â€” {community.name}</h4>
    <PostInput community={community} />
    <div>
      {posts.map(p => <div key={p._id} style={{border:'1px solid #ddd',padding:8,margin:8}}>
        <div><strong>{p.author?.name || 'Unknown'}</strong> <small>{new Date(p.createdAt).toLocaleString()}</small></div>
        <div>{p.text}</div>
        {p.attachmentUrl && <div><a href={p.attachmentUrl} target='_blank' rel='noreferrer'>Attachment</a></div>}
      </div>)}
    </div>
  </div>;
}
