import React, { useEffect, useState } from 'react';
import {
  Box,
  Heading,
  Text,
  VStack,
  SimpleGrid,
  Button,
  useToast,
  Input,
  useColorModeValue,
} from '@chakra-ui/react';
import api from '../api';

export default function Home({ setCommunity }) {
  const [communities, setCommunities] = useState([]);
  const [newName, setNewName] = useState('');
  const toast = useToast();

  const cardBg = useColorModeValue('white', 'gray.800');
  const cardHover = useColorModeValue('teal.50', 'teal.900');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  useEffect(() => {
    api
      .get('/communities')
      .then((r) => setCommunities(r.data))
      .catch(() =>
        toast({
          title: 'Error loading communities',
          status: 'error',
          duration: 3000,
          isClosable: true,
        })
      );
  }, [toast]);

  const createCommunity = async () => {
    if (!newName.trim()) {
      toast({
        title: 'Please enter a name',
        status: 'warning',
        duration: 2000,
        isClosable: true,
      });
      return;
    }
    try {
      const r = await api.post('/communities', { name: newName });
      setCommunities([r.data, ...communities]);
      setNewName('');
      toast({
        title: 'Community created!',
        description: 'You can now start sharing resources.',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      toast({
        title: 'Error creating community',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  return (
    <Box maxW="5xl" mx="auto" px={5} py={10}>
      <Heading mb={6} textAlign="center">
        Schoolazon Communities
      </Heading>

      <VStack mb={8}>
        <Text color="gray.600" fontSize="lg" textAlign="center">
          Join your schoolâ€™s community or create a new one.
        </Text>

        <Box display="flex" w="full" maxW="md" mt={4}>
          <Input
            placeholder="Create new community..."
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            mr={2}
          />
          <Button colorScheme="teal" onClick={createCommunity}>
            Create
          </Button>
        </Box>
      </VStack>

      <SimpleGrid columns={[1, 2, 3]} spacing={6}>
        {communities.map((c) => (
          <Box
            key={c._id}
            p={6}
            bg={cardBg}
            border="1px solid"
            borderColor={borderColor}
            borderRadius="2xl"
            shadow="md"
            _hover={{ bg: cardHover, transform: 'translateY(-3px)' }}
            transition="all 0.2s"
            cursor="pointer"
            onClick={() => setCommunity(c)}
          >
            <Heading size="md" mb={2}>
              {c.name}
            </Heading>
            <Text color="gray.500">Click to enter</Text>
          </Box>
        ))}
      </SimpleGrid>
    </Box>
  );
}
