import React, { useState, useEffect } from "react";
import {
  Box,
  Heading,
  VStack,
  Text,
  HStack,
  Avatar,
  Divider,
  Spinner,
  Card,
  CardBody,
  useColorModeValue,
} from "@chakra-ui/react";
import api from "../api";
import socket from "../socket";
import PostInput from "./PostInput";

export default function CommunityFeed({ community }) {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const cardBg = useColorModeValue("white", "gray.800");
  const borderColor = useColorModeValue("gray.200", "gray.700");

  useEffect(() => {
    if (!community) return;
    let cancelled = false;

    setLoading(true);
    api
      .get("/posts?community=" + community._id)
      .then((r) => {
        if (!cancelled) setPosts(r.data);
        setLoading(false);
      })
      .catch(() => setLoading(false));

    // connect socket and join room
    const token = localStorage.getItem("token");
    socket.connect();
    socket.emit("join_community", { communityId: community._id, token });
    socket.on("new_post", (p) => {
      setPosts((prev) => [p, ...prev]);
    });

    return () => {
      cancelled = true;
      socket.off("new_post");
      socket.disconnect();
    };
  }, [community]);

  if (!community)
    return (
      <Box textAlign="center" py={10}>
        <Spinner size="xl" color="blue.400" />
        <Text mt={4}>Loading community...</Text>
      </Box>
    );

  return (
    <Box
      bgGradient="linear(to-br, blue.50, purple.50)"
      minH="100vh"
      py={10}
      px={6}
    >
      <Box
        maxW="4xl"
        mx="auto"
        bg={cardBg}
        borderRadius="2xl"
        boxShadow="lg"
        p={8}
        border="1px solid"
        borderColor={borderColor}
      >
        <Heading size="lg" color="blue.700" mb={6}>
          {community.name} Feed
        </Heading>

        <PostInput community={community} />

        <Divider my={6} />

        {loading ? (
          <VStack py={10}>
            <Spinner size="xl" color="blue.500" />
          </VStack>
        ) : posts.length === 0 ? (
          <Text color="gray.500" textAlign="center" py={6}>
            No posts yet â€” be the first to share something!
          </Text>
        ) : (
          <VStack align="stretch" spacing={4}>
            {posts.map((p) => (
              <Card
                key={p._id}
                bg={useColorModeValue("white", "gray.800")}
                borderRadius="xl"
                border="1px solid"
                borderColor={borderColor}
                shadow="sm"
                _hover={{ shadow: "md" }}
              >
                <CardBody>
                  <HStack spacing={3}>
                    <Avatar
                      name={p.author?.name || "Unknown"}
                      bg="blue.400"
                      color="white"
                      size="sm"
                    />
                    <Box>
                      <Text fontWeight="bold">
                        {p.author?.name || "Unknown"}
                      </Text>
                      <Text fontSize="sm" color="gray.500">
                        {new Date(p.createdAt).toLocaleString()}
                      </Text>
                    </Box>
                  </HStack>
                  <Text mt={3} fontSize="md">
                    {p.text}
                  </Text>
                  {p.attachmentUrl && (
                    <Text mt={2}>
                      ðŸ“Ž{" "}
                      <a
                        href={p.attachmentUrl}
                        target="_blank"
                        rel="noreferrer"
                        style={{ color: "#3182CE" }}
                      >
                        View Attachment
                      </a>
                    </Text>
                  )}
                </CardBody>
              </Card>
            ))}
          </VStack>
        )}
      </Box>
    </Box>
  );
}
