import React, { useState } from 'react';
import api from '../api';

export default function Signup({ setToken }){
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [profilePic,setProfilePic]=useState(null);
  const [profilePicUrl,setProfilePicUrl]=useState('');
  const [uploading,setUploading]=useState(false);
  const [msg,setMsg]=useState('');

  const handlePicUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setUploading(true);
    const fd = new FormData();
    fd.append('file', file);
    try {
      const res = await api.post('/auth/upload-profile-pic', fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setProfilePicUrl(res.data.profilePic);
    } catch {
      setMsg('Profile pic upload failed');
    }
    setUploading(false);
  };

  const submit = async (e)=>{
    e.preventDefault();
    try{
      const r = await api.post('/auth/signup', { name, email, password, profilePic: profilePicUrl });
      setToken(r.data.token);
    }catch(err){ setMsg(err.response?.data?.error || 'Error'); }
  };
  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-body">
          <h3 className="card-title mb-3">Signup</h3>
          <form onSubmit={submit}>
            <div className="mb-3 text-center">
              <img
                src={profilePicUrl || 'https://via.placeholder.com/100?text=Avatar'}
                alt="Profile"
                className="rounded-circle border mb-2"
                style={{ width: 100, height: 100, objectFit: 'cover' }}
              />
              <input type="file" accept="image/*" onChange={handlePicUpload} disabled={uploading} />
              {uploading && <span className="ms-2">Uploading...</span>}
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                placeholder="Name"
                value={name}
                onChange={e => setName(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                placeholder="Email"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                type="password"
                placeholder="Password"
                value={password}
                onChange={e => setPassword(e.target.value)}
              />
            </div>
            <button className="btn btn-primary" type="submit">Signup</button>
          </form>
          {msg && <div className="mt-3 alert alert-danger">{msg}</div>}
        </div>
      </div>
      <div className="mt-3 text-center">
        <span>Already have an account? </span>
        <a href="/login" className="text-primary">Log in</a>
      </div>
    </div>
  );
}
