import React, { useState, useEffect } from 'react';
import api from '../api';
import CommunityFeed from './CommunityFeed';

export default function Home({ token, setToken }){
  const [communities, setCommunities] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(()=>{ fetchCommunities(); }, []);

  async function fetchCommunities(q=''){
    const res = await api.get('/communities?q='+encodeURIComponent(q));
    setCommunities(res.data);
  }

  return <div style={{padding:20}}>
    <button onClick={()=>{ localStorage.removeItem('token'); setToken(null); }}>Logout</button>
    <h2>Communities</h2>
    <ul>{communities.map(c=> <li key={c._id} onClick={()=>setSelected(c)} style={{cursor:'pointer'}}>{c.name} ({c.grade || ''})</li>)}</ul>
    <CreateCommunity onCreated={fetchCommunities} />
    {selected && <div>
      <h3>{selected.name}</h3>
      <CommunityFeed community={selected} />
    </div>}
  </div>;
}

function CreateCommunity({ onCreated }){
  const [name,setName]=useState(''); const [grade,setGrade]=useState(''); const [school,setSchool]=useState('');
  const submit = async e=>{ e.preventDefault(); await api.post('/communities', { name, grade, schoolName:school }); setName(''); setGrade(''); setSchool(''); onCreated(); };
  return <div><h4>Create community</h4>
    <form onSubmit={submit}>
      <input placeholder='Community name' value={name} onChange={e=>setName(e.target.value)} /> <input placeholder='Grade' value={grade} onChange={e=>setGrade(e.target.value)} /> <input placeholder='School' value={school} onChange={e=>setSchool(e.target.value)} /><button>Create</button>
    </form>
  </div>;
}
