import React, { useState } from "react";
import {
  Box,
  Button,
  Input,
  Heading,
  Text,
  VStack,
  useToast,
  FormControl,
  FormLabel,
  Container,
  Link,
} from "@chakra-ui/react";
import api from "../api";

export default function Signup({ setToken }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const toast = useToast();

  const submit = async (e) => {
    e.preventDefault();
    if (!name || !email || !password) {
      toast({
        title: "All fields are required.",
        status: "warning",
        duration: 3000,
      });
      return;
    }

    try {
      const r = await api.post("/auth/signup", { name, email, password });
      setToken(r.data.token);
      toast({
        title: "Signup successful!",
        description: "Redirecting you to your dashboard...",
        status: "success",
        duration: 3000,
      });
    } catch (err) {
      toast({
        title: err.response?.data?.error || "Error during signup.",
        status: "error",
        duration: 3000,
      });
    }
  };

  return (
    <Box
      bgGradient="linear(to-br, purple.50, blue.50)"
      minH="100vh"
      py={20}
      display="flex"
      alignItems="center"
      justifyContent="center"
    >
      <Container maxW="md">
        <Box
          bg="white"
          p={10}
          borderRadius="2xl"
          boxShadow="2xl"
          border="1px solid"
          borderColor="gray.100"
        >
          <Heading size="lg" color="blue.700" mb={6} textAlign="center">
            Create Your Schoolazon Account
          </Heading>

          <form onSubmit={submit}>
            <VStack spacing={4} align="stretch">
              <FormControl>
                <FormLabel>Name</FormLabel>
                <Input
                  placeholder="Enter your name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  focusBorderColor="blue.400"
                />
              </FormControl>

              <FormControl>
                <FormLabel>Email</FormLabel>
                <Input
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  focusBorderColor="blue.400"
                />
              </FormControl>

              <FormControl>
                <FormLabel>Password</FormLabel>
                <Input
                  type="password"
                  placeholder="Enter a secure password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  focusBorderColor="blue.400"
                />
              </FormControl>

              <Button colorScheme="blue" type="submit" w="full">
                Sign Up
              </Button>
            </VStack>
          </form>

          <Text textAlign="center" mt={6} color="gray.600">
            Already have an account?{" "}
            <Link color="blue.500" href="/login">
              Log in
            </Link>
          </Text>
        </Box>
      </Container>
    </Box>
  );
}
